mapper.select:
select 

from shanduo_collect
where user_id = #{0}
and file_url = #{1}

mapper.delete:
 delete from shanduo_collect
	where user_id = #{0}
	and id = #{1}

mapper.select:
select 
id,file_url as fileUrl,create_date as createDate
from shanduo_collect
where user_id = #{0}
order by create_date desc
limit #{1},#{2}
